import React from 'react'
import { useState } from 'react'

const DataContext =React.createContext ({
  sendData:[],
  updateData:(newData)=>{},
  acceptData:(column,row,value)=>{},
  savedData:[]
})

export default DataContext


export const DataContextProvider=(props)=>{
    const [sendData,setSendData]=useState([])
    
    const updateData=(newData)=>{
        console.log('newdata',newData);
        console.log('senddata',sendData);
        

        const existinItem=sendData.find(item=>{return item.loanId===newData.loanId})
        console.log(existinItem);
        
        if (!existinItem){
            setSendData((prevState)=>([...prevState,newData]));    
        } else {
                existinItem.loanId=newData.loanId
                existinItem.Status=newData.Status
                existinItem.packN=newData.packN
                existinItem.boxN=newData.boxN
        }
        
                   
    }

    const [savedData,setSavedData]=useState([])  // {id , status,packN,boxN}
    
  const acceptHandlerF =(column, row, value)=>{
    
    const existinItem=savedData.find(item=>{return item.id===row.LoanID})
    if (!existinItem){
      setSavedData((prevState)=>([...prevState,Object.assign({id:row.LoanID, [column]:column=value})]));
       } 
       else { 
                existinItem.id=row.LoanID;
                existinItem[column]=value
            }

    //    colorHandler(row,false)
  }

    return <DataContext.Provider value={{
        updateData:updateData,
        sendData:sendData,
        acceptData:acceptHandlerF,
        savedData:savedData
    }} >
        {props.children}
    </DataContext.Provider>    
}
