import React from 'react'
import DataTable from 'react-data-table-component';

import Select from 'react-select'

const options = [
  { value: 'chocolate', label: 'Chocolate' },
  { value: 'strawberry', label: 'Strawberry' },
  { value: 'vanilla', label: 'Vanilla' }
]


export const LoanTable = (props) => {
  console.log(props.loans);
  
  const handleButtonClick = (e, LoanID) => {
    e.preventDefault();
    console.log("Row Id", LoanID);
};
  const handleRowSelected = (row) => {
    console.log(row.selectedRows.map((row)=>
    row.LoanAgreementNo));
    
    
    };
  
    // Check current page has selected items
    
  
  const columns=[
    {
      name: 'LoanID',
      selector: row => row.LoanID,
  },
    {
      name: 'FullName',
      selector: row => row.FullName,
  },
    {
      name: 'CardNumber',
      selector: row => row.CardNumber,
  },
    {
      name: 'LoanAgreementNo',
      selector: row => row.LoanAgreementNo,
  },
    {
      name: 'DisburseDate',
      selector: row => row.DisburseDate,
  },
    {
      name: 'CloseDate',
      selector: row => row.CloseDate,
  },
    {
      name: 'LoanNode',
      selector: row => row.LoanNode,
  },
  {
    name: "Status",
    button: true,
    cell: (row) => (
        <button onClick={(e) => handleButtonClick(e, row.LoanID)}
        >
          add
          </button>
    ),
}
  ];

  const data = props.loans
    


  return (

     <DataTable columns={columns} data={data} //selectableRows 
    // onSelectedRowsChange={handleRowSelected} 
    
    />
  )
}
